mapper.select:
SELECT * FROM dir_dataitem t WHERE TRUE
AND t.`dataset_id` = #{datasetId}
t.`dataset_id` IS NULL

SELECT t.* FROM dir_dataset t LEFT JOIN dir_dataset_classify_map dcm ON dcm.`dataset_id` = t.`id` LEFT JOIN dir_classify dc ON dc.`id` = dcm.`classify_id` WHERE TRUE
AND dc.`id` = #{classifyId}
AND dc.`id` IS NULL

SELECT ddtc.`id` AS columnId, ddtc.`column_en_name` AS columnName, ddtc.`column_cn_name` AS columnCnName, ddtc.`column_desc` AS columnDesc FROM drap_db_table_column ddtc WHERE TRUE
AND ddtc.`table_id` = #{tableId}
AND ddtc.`table_id` IS NULL

SELECT ddi.`id`, ddi.`db_name` AS dbName, ddi.`db_cn_name` AS DB_CN_NAME, ddi.`db_type` AS dbType, ddi.`ip_address` AS ipAddress, ddi.`user_name` AS userName, ddi.`password`, ddi.`port`, ddi.`sid`, ddi.`service_name` AS serviceName, dis.`system_name` AS systemName FROM drap_db_info ddi LEFT JOIN drap_db_system_map dsm ON dsm.`db_id` = ddi.`id` LEFT JOIN drap_info_system dis ON dis.`id` = dsm.`info_system_id` WHERE TRUE
AND dis.`id` = #{systemId}
AND dis.`id` IS NULL

SELECT dis.id, dis.`system_name` AS systemName, dis.`build_dept` AS buildDept FROM drap_info_system dis LEFT JOIN drap_system_use_dept dsud ON dsud.`system_id` = dis.`id` LEFT JOIN sys_dept sd ON sd.`id` = dsud.`dept_id` WHERE TRUE
AND dsud.`dept_id` = #{deptId}
AND dsud.`dept_id` IS NULL

SELECT
  ddti.*
FROM
  drap_db_table_info ddti
  LEFT JOIN drap_db_system_map ddsm
    ON ddsm.`db_id` = ddti.`db_id`
WHERE TRUE
   AND ddti.`db_id` = #{dbId}
   AND ddsm.`info_system_id` = #{systemId}
   AND ddti.`table_name` = #{tableName}
   OR ddti.`table_cn_name` = #{tableName}
