mapper.select:
SELECT ddc.*, dc.`classify_structure_name` AS classify_name, dt.`dataset_name`, su.`real_name` AS corrector_name, sur.`real_name` AS auditor_name FROM dir_data_correction ddc LEFT JOIN dir_dataset_classify_map dcm ON dcm.`id` = ddc.`dcm_id` LEFT JOIN dir_classify dc ON dc.`id` = dcm.`classify_id` LEFT JOIN dir_dataset dt ON dt.`id` = dcm.`dataset_id` LEFT JOIN sys_user su ON su.`id` = ddc.`corrector_id` LEFT JOIN sys_user sur ON sur.`id` = ddc.`auditor_id` WHERE ddc.`dcm_id` = #{dcmId}
AND su.`real_name` LIKE CONCAT('%',#{searchKey},'%')
ORDER BY ddc.`correct_date` DESC
